import React, { useState, useEffect, useCallback } from 'react';
import { Knob } from 'react-rotary-knob';
import * as skins from 'react-rotary-knob-skin-pack';

const WebSynth = () => {
  const [audioContext, setAudioContext] = useState(null);
  const [oscillator, setOscillator] = useState(null);
  const [gainNode, setGainNode] = useState(null);
  const [filterNode, setFilterNode] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [frequency, setFrequency] = useState(440);
  const [filterFrequency, setFilterFrequency] = useState(1000);
  const [waveform, setWaveform] = useState('sine');

  const setupAudio = useCallback(() => {
    if (audioContext) return;

    try {
      const context = new (window.AudioContext || window.webkitAudioContext)();
      setAudioContext(context);

      const osc = context.createOscillator();
      osc.type = waveform;
      osc.frequency.setValueAtTime(frequency, context.currentTime);

      const gain = context.createGain();
      gain.gain.setValueAtTime(0, context.currentTime);

      const filter = context.createBiquadFilter();
      filter.type = 'lowpass';
      filter.frequency.setValueAtTime(filterFrequency, context.currentTime);

      osc.connect(filter);
      filter.connect(gain);
      gain.connect(context.destination);

      setOscillator(osc);
      setGainNode(gain);
      setFilterNode(filter);

      osc.start();
    } catch (error) {
      console.error("Failed to setup audio:", error);
    }
  }, [audioContext, frequency, filterFrequency, waveform]);

  useEffect(() => {
    return () => {
      if (oscillator) {
        oscillator.stop();
        oscillator.disconnect();
      }
      if (gainNode) gainNode.disconnect();
      if (filterNode) filterNode.disconnect();
      if (audioContext) audioContext.close();
    };
  }, [oscillator, gainNode, filterNode, audioContext]);

  const handlePlayStop = useCallback(() => {
    if (!audioContext) {
      setupAudio();
      return; // Exit early to give setupAudio time to complete
    }

    try {
      if (audioContext.state === 'suspended') {
        audioContext.resume();
      }

      if (isPlaying) {
        gainNode?.gain.setTargetAtTime(0, audioContext.currentTime, 0.015);
        setIsPlaying(false);
      } else {
        gainNode?.gain.setTargetAtTime(0.5, audioContext.currentTime, 0.015);
        setIsPlaying(true);
      }
    } catch (error) {
      console.error("Error in handlePlayStop:", error);
    }
  }, [audioContext, isPlaying, gainNode, setupAudio]);

  const handleFrequencyChange = useCallback((newFrequency) => {
    setFrequency(newFrequency);
    if (oscillator && audioContext) {
      oscillator.frequency.setValueAtTime(newFrequency, audioContext.currentTime);
    }
  }, [oscillator, audioContext]);

  const handleFilterChange = useCallback((newFilterFrequency) => {
    setFilterFrequency(newFilterFrequency);
    if (filterNode && audioContext) {
      filterNode.frequency.setValueAtTime(newFilterFrequency, audioContext.currentTime);
    }
  }, [filterNode, audioContext]);

  const handleWaveformChange = useCallback((newWaveform) => {
    setWaveform(newWaveform);
    if (oscillator) {
      oscillator.type = newWaveform;
    }
  }, [oscillator]);

  const renderKey = useCallback((note, freq, color) => (
    <button
      className={`w-12 h-24 ${color} text-white font-bold rounded-b-lg focus:outline-none`}
      onMouseDown={() => {
        if (!audioContext) setupAudio();
        handleFrequencyChange(freq);
        if (!isPlaying) handlePlayStop();
      }}
      onMouseUp={() => {
        if (isPlaying) handlePlayStop();
      }}
    >
      {note}
    </button>
  ), [audioContext, setupAudio, handleFrequencyChange, isPlaying, handlePlayStop]);

  return (
    <div className="p-4 bg-gray-800 rounded-lg shadow-xl w-96">
      <div className="bg-gray-900 p-4 rounded-t-lg">
        <div className="bg-green-200 h-16 rounded-sm flex items-center justify-center text-2xl font-mono">
          {isPlaying ? 'Playing' : 'Stopped'} - {waveform} - {Math.round(frequency)}Hz
        </div>
      </div>
      <div className="mt-4 flex justify-around">
        <Knob
          value={frequency}
          onChange={handleFrequencyChange}
          min={20}
          max={2000}
          unlockDistance={0}
          preciseMode={false}
          skin={skins.s10}
          style={{ width: '60px', height: '60px' }}
        />
        <Knob
          value={filterFrequency}
          onChange={handleFilterChange}
          min={20}
          max={20000}
          unlockDistance={0}
          preciseMode={false}
          skin={skins.s10}
          style={{ width: '60px', height: '60px' }}
        />
      </div>
      <div className="mt-4 flex justify-around">
        <button
          className="bg-red-500 text-white px-4 py-2 rounded"
          onClick={handlePlayStop}
        >
          {isPlaying ? 'Stop' : 'Play'}
        </button>
        <select
          className="bg-blue-500 text-white px-4 py-2 rounded"
          value={waveform}
          onChange={(e) => handleWaveformChange(e.target.value)}
        >
          <option value="sine">Sine</option>
          <option value="square">Square</option>
          <option value="sawtooth">Sawtooth</option>
          <option value="triangle">Triangle</option>
        </select>
      </div>
      <div className="mt-4 flex justify-between">
        {renderKey('C', 261.63, 'bg-red-500')}
        {renderKey('D', 293.66, 'bg-orange-500')}
        {renderKey('E', 329.63, 'bg-yellow-500')}
        {renderKey('F', 349.23, 'bg-green-500')}
        {renderKey('G', 392.00, 'bg-blue-500')}
        {renderKey('A', 440.00, 'bg-indigo-500')}
        {renderKey('B', 493.88, 'bg-purple-500')}
      </div>
    </div>
  );
};

export default WebSynth;
